(31514, 'Tritron plus 30 caps.', 'تريترون بلس 30 كبسولة', '95', '125', 'Iron+folic acid+vitamin b6+vitamin b12', 'Nutrixia > warnex', 'Cap', 'كبسولة', '', 'Iron supplement', '', 'dose: 1 capsule daily.', '6224011010078', '', 2863, '2025-05-20', CURRENT_TIMESTAMP),
(25580, 'Qv face gentle cleanser 250 ml', 'كيو في جل منظف للوجه 250مل', '', '271', '', 'Ego pharmaceuticals pty ltd. > vamer pharma', 'Cleanser', 'منظف', '', 'Skin care', '250 ml', 'mild soap-free facial cleanser. enriched with safflower oil this gentle and lightly foaming cleanser is ideal for dry and sensitive.', '', '', 642, '2022-08-04', CURRENT_TIMESTAMP),
(637, 'Amoxil 1gm vial', 'اموكسيل 1جم فيال', '', '7', 'Amoxicillin', 'Mup > smithkline beecham', 'Vial', 'فيال', '', 'Penicillins.monocomponent', '1gm', 'Amoxicillin alone is indicated to treat susceptible bacterial infections of the ear, nose, throat, genitourinary tract, skin, skin structure, and lower respiratory tract.[L11656,L11659] Amoxicillin is given with calvulanic acid to treat acute bacterial sinusitis, community acquired pneumonia, lower respiratory tract infections, acute bacterial otitis media, skin and skin structure infections, and urinary tract infections.[L11650,L7880,L11653] Amoxicillin is given with omeprazole in the treatment of _Helicobacter pylori_ (_H. pylori_) infection.[L9743,L11647]
Amoxicillin is used in combination with [vonoprazan] and [clarithromycin] as co-packaged triple therapy or in combination with [vonoprazan] as co-packaged dual therapy to treat _H. pylori_ infection in adults.[L41695]', '', '', 1493, '2023-05-05', CURRENT_TIMESTAMP),
(1613, 'Bivacyn 150 gm powder spray', 'بيفاسين 150 جم بخاخ بودرة', '', '29', 'Neomycin-bacitracin', 'Egyptian co. for adv. pharma. (ecap) > acdima international trading', 'Spray', 'بخاخ', '', '', '150 gm', '', '6224000588007', '', 1809, '2022-12-03', CURRENT_TIMESTAMP),
