(2132, 'Carvid 6.25mg 20 tab.', 'كارفيد 6.25مجم 20 قرص', '', '30', 'Carvedilol', 'Multi-apex', 'Tab', 'أقراص', 'علاج ارتفاع ضغط الدم', 'Antihypertensive.beta blocker.unselective', '6.25mg', 'Carvedilol is indicated to treat mild to severe heart failure, left ventricular dysfunction after myocardial infarction with ventricular ejection fraction ≤40%, or hypertension.[L7889,L7892]', '6223003201333', '', 4886, '2023-05-28', CURRENT_TIMESTAMP),
(29638, 'Welevon 10 caps.', 'ويليفون 10 كبسول', '', '14', 'Feverfew+white willow+estre c+vitamin b6', 'Debeiky', 'Cap', 'كبسولة', '', '', '', 'used as adjuvant to drugs used in headache and arthritis. dose: 2 caps. as initial dose then 1 cap twice daily.', '', '', 412, '2022-08-06', CURRENT_TIMESTAMP),
(22713, 'Miconaz 400mg 3 vaginal ovules', 'ميكوناز 400مجم 3 بويضات مهبلية', '', '14', 'Miconazole', 'Mup', 'Unknown', 'غير محدد', '', 'Antifungals.imidazoles', '400mg', 'Miconazole is indicated for the local treatment of oropharyngeal candidiasis in adult patients and for the adjunctive treatment of diaper dermatitis complicated by candidiasis in immunocompetent patients aged four weeks and older.[L14021, L14024] Miconazole is available as both a suppository and cream for the treatment of vaginal yeast infections and the relief of associated vulvar itching and irritation.[L14027] Lastly, miconazole cream is effective in treating athlete''s foot (tinea pedis), jock itch (tinea cruris), ringworm infections (tinea corporis),[L14033] pityriasis (formerly tinea) versicolor,[A203633] and cutaneous candidiasis.[A203630]', '6221508150514', '', 1316, '2022-12-06', CURRENT_TIMESTAMP),
(21692, 'Lemsisart 40mg 14 tabs.', 'لمسيسارت 40 مجم 14 قرص', '', '34', 'Telmisartan', 'Global napi pharmaceuticals', 'Tab', 'أقراص', '', 'Antihypertensive.angiotensin blocker', '40mg', 'Used alone or in combination with other classes of antihypertensives for the treatment of hypertension.[L44652] Also used in the treatment of diabetic nephropathy in hypertensive patients with type 2 diabetes mellitus, as well as the treatment of congestive heart failure (only in patients who cannot tolerate ACE inhibitors).[A255892]', '', '', 846, '2022-07-24', CURRENT_TIMESTAMP),
