(7659, 'Mafepain 500mg 10 f.c. tab.', 'مافيبين 500مجم 10 اقراص', '', '6', 'Mefenamic acid', 'Spimaco > eimc', 'Tab', 'أقراص', '', 'Nsaid.fenamates', '500mg', 'For the treatment of rheumatoid arthritis, osteoarthritis, dysmenorrhea, and mild to moderate pain, inflammation, and fever.', '', '', 945, '2022-08-15', CURRENT_TIMESTAMP),
(26236, 'Rocephin 500mg i.m.vial (n/a)', 'روسيفين 500مجم فيال عضل', '', '31', 'Ceftriaxone', 'Eipico > f.hoffman la roche', 'Vial', 'فيال', '', 'Antibiotic.cephalosporin.third-generation', '500mg', 'Ceftriaxone is used for the treatment of the infections (respiratory, skin, soft tissue, UTI, ENT) caused by susceptible organisms.[L15082] Organisms that are generally susceptible to ceftriaxone include S. pneumoniae, S. pyogenes (group A beta-hemolytic streptococci), coagulase-negative staphylococci, Some Enterobacter spp, H. influenzae, N. gonorrhoeae, P. mirabilis, E. coli, Klebsiella spp, M. catarrhalis, B. burgdorferi, and some oral anaerobes.[A215582]', '', '', 726, '2023-01-24', CURRENT_TIMESTAMP),
(27264, 'Solupred oro 5mg 30 orodispersible tab.', 'سوليوبريد اورو 5مجم 30 قرص ذائب بالفم', '65', '84', 'Prednisolone', 'Sanofi winthrop > sanofi', 'Tab', 'أقراص', 'علاج الحساسية الشديدة -- علاج الامراض التي تحتاج الي كورتيزون', 'Glucocorticoid', '5mg', 'Prednisolone is indicated to treat endocrine, rheumatic, and hematologic disorders; collagen, dermatologic, ophthalmic, respiratory, and gastrointestinal diseases; allergic and edematous states; and other conditions like tuberculous meningitis.[L9542]', '3664798012231', '', 4245, '2025-06-23', CURRENT_TIMESTAMP),
(17142, 'Debacozolin 600mg 10 f.c. tab.', 'ديباكوزولين 600مجم 10 اقراص', '', '71', 'Linezolid', 'Debeiky', 'Tab', 'أقراص', '', 'Antibiotic.oxazolidinone', '600mg', 'Linezolid is indicated in adults and children for the treatment of infections caused by susceptible Gram-positive bacteria, including nosocomial pneumonia, community-acquired pneumonia, skin and skin structure infections, and vancomycin-resistant _Enterococcus faecium_ infections.[L32965] Examples of susceptible bacteria include _Staphylococcus aureus_, _Streptococcus pneumoniae_, _Streptococcus pyogenes_, and _Streptococcus agalactiae_.[L32965]
Linezolid is not indicated for the treatment of Gram-negative infections, nor has it been evaluated for use longer than 28 days.[L32965]', '', '', 1170, '2022-09-19', CURRENT_TIMESTAMP),
