(23030, 'Moxifloxacin - el nile 400mg/250ml sol. for inf.', 'موكسيفلوكساسين 400مجم /250مل محلول للاطفال', '', '81', 'Moxifloxacin', 'El nile.', 'Sol', 'محلول', '', 'Antibiotic.quinolone', '400mg', 'For the treatment of sinus and lung infections such as sinusitis, pneumonia, and secondary infections in chronic bronchitis. Also for the treatment of bacterial conjunctivitis (pinkeye).', '', '', 816, '2022-08-02', CURRENT_TIMESTAMP),
(10876, 'Ojera - skin emollient 50 gm cream', 'اوجيرا كريم ملطف للجلد 50 جم', '', '45', 'Tea tree oil+honey+panthenol+vit. e+olive oil+beeswax', 'Merk share', 'Cream', 'كريم', '', 'Skin emollient& moisturizing cream', '50 gm', 'Indicated for topical use to help protect against infection in minor cuts, scrapes, and burns. No FDA-approved therapeutic indications.', '', '', 918, '2024-06-15', CURRENT_TIMESTAMP),
