(11519, 'Top hair lotion plus 120 ml', 'توب هير لوشن بلس 120 مل', '200', '230', 'Caffeine+argan oil+ginseng+wheat germ oil+vitamin e+biotin+garlic oil+jojoba oil+castor oil+olive oil+panthenol', 'Egyptian co. for cosmetics > infinity pharm', 'Lotion', 'لوشن', '', 'Hair care', '120 ml', 'provides a natural approach to hair loss with an invigorating formula that delivers longer stronger healthier hair and scalp. inhibits the production of the hormone dht associated with androgenic alopecia. stimulates microcirculation promotes healthy hair', '6222019614304', '', 1323, '2025-10-27', CURRENT_TIMESTAMP),
(25802, 'Regna tea 15 envelop', 'ريجنا شاي 15 كيس', '', '15', 'Ginger oil', 'Apotix pharmaceuticals', 'Unknown', 'غير محدد', '', 'Git disturbances', '', 'used for: -ibs -abdominal pain -flatulence and indigestion', '', '', 770, '2022-07-24', CURRENT_TIMESTAMP),
(16010, 'Cefepime 500 mg i.m./i.v. vial', 'سيفيبيم 500 مجم فيال', '33.5', '50', 'Cefepime', 'Pharco b', 'Vial', 'فيال', '', 'Antibiotic.cephalosporin.fourth-generation', '500 mg', 'Cefepime is indicated for the treatment of pneumonia caused by susceptible bacteria, and for empiric therapy for febrile neutropenic patients. Cefepime is also indicated for the treatment of uncomplicated and complicated urinary tract infections (cUTI) including pyelonephritis, uncomplicated skin and skin structure infections, and complicated intra-abdominal infections (used in combination with [metronidazole]) in adults caused by susceptible bacteria.[L42095,L42100]
Cefepime is also used in combination with [enmetazobactam] to treat cUTI.[L50117]', '6222012215003', '', 864, '2025-01-09', CURRENT_TIMESTAMP),
(5672, 'Glycerin pure 60 ml', 'جلسرين طبي نقي 60 مل', '', '20', 'Glycerin', 'Company', 'Unknown', 'غير محدد', '', 'Emollient', '60 ml', 'It is used as a solvent, emollient, pharmaceutical agent, and sweetening agent.', '6222018805543', '', 1760, '2023-02-08', CURRENT_TIMESTAMP),
(29555, 'Vivopal 500mcg 20 f.c.tab.', 'فيفوبال 500مكجم 20 اقراص', '', '9', 'Mecobalamin', 'Sigma > horizon pharma', 'Tab', 'أقراص', '', 'Vitamin b', '500mcg', 'about mecobalamin water soluble form of vitamin b12 mechanism of action of mecobalamin mecobalamin is the neurologically active form of vitamin b12 and occurs as a water-soluble vitamin in the body. it is a cofactor in the enzyme methionine synthase which', '', '', 1043, '2022-07-10', CURRENT_TIMESTAMP),
