(27178, 'Sodium chloride 0.9% (allmed) i.v. inf. 500 ml', 'صوديوم كلوريد 0.9% اولميد محلول وريدي 500 مل', '', '11', 'Sodium chloride', 'Allmed', 'Unknown', 'غير محدد', '', 'Normal saline', '0.9%', 'This intravenous solution is indicated for use in adults and pediatric patients as a source of electrolytes and water for hydration. Also, designed for use as a diluent and delivery system for intermittent intravenous administration of compatible drug additives.', '', '', 803, '2022-07-10', CURRENT_TIMESTAMP),
(22343, 'Mascglucom 5% eye drops 15 ml', 'ماسكجلوكوم قطرة عين 15مل', '', '33', 'Levobunolol', 'Sigma tec > concord pharmaceutical industries', 'Drops', 'نقط', '', 'Antiglaucoma', '5%', 'For lowering intraocular pressure (IOP) and may be used in patients with chronic open-angle glaucoma or ocular hypertension.', '', '', 727, '2022-08-02', CURRENT_TIMESTAMP);
