(28407, 'Travonorm plus 0.004%/0.5% eye drops 5 ml', 'ترافونورم بلس 0.004%/0.55 قطرة للعين 5مل', '', '123', 'Timolol+travoprost', 'Orchidia pharmaceutical industries', 'Drops', 'نقط', '', 'Antiglaucoma', '0.004%', 'about timolol nonselective beta adrenergic blocker ophthalmic- antiglaucoma antimigraine. mechanism of action of timolol timolol is a non selective beta blocker. it inhibits the beta receptors (?2) present in the eye such as in the ciliary body epithelium', '', '', 950, '2022-07-25', CURRENT_TIMESTAMP),
(18381, 'Epicocillin 250mg/5ml susp. 60 ml', 'ابيكوسيللين 250مجم/5مل معلق 60 مل', '', '7', 'Ampicillin', 'Eipico', 'Susp', 'معلق', '', 'Penicillins.monocomponent', '250mg', 'For treatment of infection (Respiratory, GI, UTI and meningitis) due to E. coli, P. mirabilis, enterococci, Shigella, S. typhosa and other Salmonella, nonpenicillinase-producing N. gononhoeae, H. influenzae, staphylococci, streptococci including streptoc', '', '', 998, '2023-01-07', CURRENT_TIMESTAMP),
