INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (2035, 6309, 'Levodopa', 'Neuroleptic Malignant Syndrome', 'The use of dopamine agonists is contraindicated in patients with neuroleptic malignant syndrome (NMS).  NMS is characterized by hyperthermia, muscle rigidity, altered mental status, irregular pulse or blood pressure, tachycardia, and diaphoresis.  The syndrome may rarely be precipitated by abrupt discontinuation of the dopamine agonist.', '3', 'Friedman JH, Feinberg SS, Feldman RG "A neuroleptic malignantlike syndrome due to levodopa therapy  withdrawal." JAMA 254 (1985):  2792-5|Genis D "Neuroleptic malignant syndrome: impaired dopaminergic systems?" Neurology 35 (1985):  1806|Rainer C, Scheinost NA, Lefeber EJ "Neuroleptic malignant syndrome. When levodopa withdrawal is the  cause." Postgrad Med 89 (1991):  175-8,|Gibb WR, Griffith DN "Levodopa withdrawal syndrome identical to neuroleptic malignant  syndrome." Postgrad Med J 62 (1986):  59-60|"Product Information. Mirapex (pramipexole)." Boehringer Ingelheim  (2001):', 'DDInter', 0);
