INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (2095, 15058, 'Leucovorin', 'Kidney Diseases', 'Leucovorin is excreted by the kidney and the risk of toxic reactions to the drug may be greater in patients with impaired renal function.  Leucovorin should be used cautiously on these patients, and care should be taken in dose selection.  Monitoring of renal function is recommended.', '2', '"Product Information. Wellcovorin (leucovorin)." Glaxo Wellcome, Research Triangle Park, NC.', 'DDInter', 0);
