INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (2953, 16758, 'Levonorgestrel', 'Intracranial Hypertension', 'The use of levonorgestrel contraceptive implants is contraindicated in patients with a current or past history of idiopathic intracranial hypertension (pseudotumor cerebri, benign intracranial hypertension).  This disorder is most commonly seen in obese women of reproductive age and has been reported in users of levonorgestrel implants.  Patients who experience symptoms of this disorder (e.g., headaches associated with changing frequency, pattern, severity or persistence; visual disturbances) while on levonorgestrel therapy should be referred to a neurologist.  If the diagnosis is confirmed, the implants should be removed permanently.', '3', '"Product Information. Triphasil (ethinyl estradiol-levonorgestrel)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Norplant System (levonorgestrel)." Wyeth-Ayerst Laboratories  (2001):', 'DDInter', 0);
