INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (2988, 16758, 'Levonorgestrel', 'Eye Diseases', 'Estrogens and progestogens may cause retinal thrombosis.  Oral contraceptives should be discontinued if there is unexplained partial or complete loss of vision; onset of proptosis or diplopia; papilledema; or retinal vascular lesions.  Therapy with these agents should be administered cautiously in patients who have preexisting ocular problems and appropriate diagnostic and therapeutic measures should be instituted.  Contact lens wearers who develop visual changes or changes in lens tolerance should be assessed by an ophthalmologist.', '2', '"Product Information. Ortho-Novum 1/35 (ethinyl estradiol-norethindrone)." Ortho McNeil Pharmaceutical|"Product Information. Ortho-Novum 10/11 (ethinyl estradiol-norethindrone)." Ortho McNeil Pharmaceutical|"Product Information. Ortho-Cept (desogestrel-ethinyl estradiol)." Ortho McNeil Pharmaceutical  (2001):|"Product Information. Demulen 1/50 (ethinyl estradiol-ethynodiol)." Searle|"Product Information. Triphasil (ethinyl estradiol-levonorgestrel)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Ortho-Cyclen (ethinyl estradiol-norgestimate)." Ortho McNeil Pharmaceutical  (2001):|"Product Information. Lo/Ovral (ethinyl estradiol-norgestrel)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Ortho-Novum 1/50 (mestranol-norethindrone)." Ortho McNeil Pharmaceutical|"Product Information. Norplant System (levonorgestrel)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Micronor (norethindrone)." Ortho McNeil Pharmaceutical  (2001):|"Product Information. Ovrette (norgestrel)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Prometrium (progesterone)." Virtus Pharmaceuticals LLC  (2001):|"Product Information. Nextstellis (drospirenone-estetrol)." Mayne Pharma  (2021):', 'DDInter', 0);
