INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (5128, 3420, 'Medroxyprogesterone acetate', 'Menorrhagia', 'The use of medroxyprogesterone in women with undiagnosed vaginal bleeding is contraindicated.  Most women using medroxyprogesterone experience disruption of menstrual bleeding patterns.  Altered menstrual bleeding patterns include amenorrhea, irregular or unpredictable bleeding or spotting, prolonged spotting or bleeding, and heavy bleeding.  Rule out the possibility of organic pathology if abnormal bleeding persists or is severe, and institute appropriate treatment.', '3', '"Product Information. Depo-Provera (medroxyprogesterone)." Pharmacia and Upjohn  (2001):', 'DDInter', 0);
