INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10555, 8679, 'Neomycin', 'Intestinal Obstruction', 'The use of oral aminoglycosides is contraindicated in patients with intestinal obstruction.  Orally administered aminoglycosides are poorly absorbed from the gastrointestinal tract and primarily eliminated unchanged in the feces.  Drug retention and enhanced systemic absorption may occur in the presence of intestinal obstruction, increasing the risk of oto- and nephrotoxicity associated with these drugs.', '3', 'Ristuccia AM, Cunha BA "The aminoglycosides." Med Clin North Am 66 (1982):  303-12|"Product Information. Humatin (paromomycin)." Parke-Davis  (2001):|"Product Information. Mycifradin (neomycin)." Emerson Laboratories  (2001):', 'DDInter', 0);
