INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10596, 27924, 'Nilotinib', 'Hepatic Insufficiency', 'Nilotinib exposure is increased in patients with hepatic impairment.  The use of a lower starting dose might be needed.  Additionally, there have been reports of hepatotoxicity as measured by elevations in bilirubin, AST, ALT and alkaline phosphatase.  Liver function should be monitored before initiation of treatment and regularly during therapy.  Dose reduction or interruption might be needed if laboratory abnormalities are found.', '2', '"Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals  (2007):', 'DDInter', 0);
