INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10600, 23973, 'Nintedanib', 'Lung Diseases', 'The use of certain multikinase inhibitors has been associated with pulmonary toxicity.  Serious cases of interstitial lung disease (ILD), including fatal cases and interstitial pneumonitis or pulmonary fibrosis have been reported.  Caution is recommended when using these agents in patients with a history of interstitial pneumonitis or pulmonary fibrosis or those patients presenting with acute onset of new or progressive unexplained pulmonary symptoms such as dyspnea, cough, and fever pending diagnostic evaluation.  If ILD is confirmed, permanently discontinue these agents and institute appropriate measures.  Immediately withhold treatment in patients diagnosed with ILD/pneumonitis and permanently discontinue therapy if no other potential causes of ILD/pneumonitis have been identified.', '2', '"Product Information. Vandetanib (vandetanib)." Astra-Zeneca Pharmaceuticals  (2011):|"Product Information. Zelboraf (vemurafenib)." Genentech  (2011):|"Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group  (2011):|"Product Information. Mekinist (trametinib)." GlaxoSmithKline  (2013):|"Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals  (2014):|"Product Information. Zydelig (idelalisib)." Gilead Sciences  (2014):|"Product Information. Alecensa (alectinib)." Genentech  (2015):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10601, 23974, 'Nintedanib', 'Lung Diseases', 'The use of certain multikinase inhibitors has been associated with pulmonary toxicity.  Serious cases of interstitial lung disease (ILD), including fatal cases and interstitial pneumonitis or pulmonary fibrosis have been reported.  Caution is recommended when using these agents in patients with a history of interstitial pneumonitis or pulmonary fibrosis or those patients presenting with acute onset of new or progressive unexplained pulmonary symptoms such as dyspnea, cough, and fever pending diagnostic evaluation.  If ILD is confirmed, permanently discontinue these agents and institute appropriate measures.  Immediately withhold treatment in patients diagnosed with ILD/pneumonitis and permanently discontinue therapy if no other potential causes of ILD/pneumonitis have been identified.', '2', '"Product Information. Vandetanib (vandetanib)." Astra-Zeneca Pharmaceuticals  (2011):|"Product Information. Zelboraf (vemurafenib)." Genentech  (2011):|"Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group  (2011):|"Product Information. Mekinist (trametinib)." GlaxoSmithKline  (2013):|"Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals  (2014):|"Product Information. Zydelig (idelalisib)." Gilead Sciences  (2014):|"Product Information. Alecensa (alectinib)." Genentech  (2015):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10602, 23973, 'Nintedanib', 'Thromboembolism', 'Arterial thromboembolic events, including myocardial infarction have been reported in patients taking nintedanib.  It is recommended to exercise caution when treating patients at higher cardiovascular risk, including known coronary artery disease and to consider treatment interruption in patients who develop signs or symptoms of acute myocardial ischemia.', '2', '"Product Information. Ofev (nintedanib)." Boehringer Ingelheim  (2014):', 'DDInter', 0);
