INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10606, 23973, 'Nintedanib', 'Peptic Ulcer Perforation', 'Nintedanib may increase the risk of gastrointestinal perforation.  Use caution when treating patients who have had recent abdominal surgery.  It is recommended to discontinue therapy with nintedanib in patients who develop gastrointestinal perforation.  Nintedanib should only be used in patients with known risk of gastrointestinal perforation if the anticipated benefit outweighs the potential risk.  Care and close monitoring is recommended.', '2', '"Product Information. Ofev (nintedanib)." Boehringer Ingelheim  (2014):', 'DDInter', 0);
