INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10942, 8827, 'Nicotine', 'Pheochromocytoma', 'Nicotine causes the release of catecholamines from the adrenal medulla.  Enhanced sympathetic activity can provoke hypertensive crises in patients with pheochromocytoma or other tumors of the adrenal medulla, such as some neuroblastomas.  Therapy with nicotine should be administered cautiously in patients with these tumors.', '3', '"Product Information. Habitrol (nicotine)." Basel Pharmaceuticals  (2001):|"Product Information. Nicorette (nicotine)." SmithKline Beecham  (2001):|"Product Information. Nicotrol NS (nicotine)." McNeil Pharmaceutical  (2001):', 'DDInter', 0);
