INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10974, 7653, 'Nitrofurantoin', 'Glucosephosphate Dehydrogenase Deficiency', 'Hemolytic anemia has been reported in patients treated with nitrofurantoin, primarily in patients with glucose-6-phosphate dehydrogenase (G-6-PD) deficiency.  Therapy with nitrofurantoin should be avoided or administered cautiously in patients with G-6-PD deficiency.  The development of hemolysis is an indication for withdrawal of nitrofurantoin therapy.  Hemolysis ceases when the drug is withdrawn.', '3', '"Product Information. Macrobid (nitrofurantoin)." Procter and Gamble Pharmaceuticals  (2002):|"Product Information. Macrodantin (nitrofurantoin)." Procter and Gamble Pharmaceuticals  (2002):', 'DDInter', 0);
