INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10990, 7653, 'Nitrofurantoin', 'Kidney Diseases', 'The use of nitrofurantoin is contraindicated in patients with anuria, oliguria, or significant renal impairment (CrCl < 60 mL/min).  Urinary concentration of the drug is likely to be inadequate in these patients, increasing the risk of therapeutic failure.  Since nitrofurantoin is eliminated by the kidney, risk of toxicity may also be increased.', '3', 'Sachs J, Geer T, Noell P, Kunin CM "Effect of renal function on urinary recovery of orally administered nitrofurantoin." N Engl J Med 278 (1967):  1032-5|Hoener B, Patterson SE "Nitrofurantoin disposition." Clin Pharmacol Ther 29 (1981):  808-16|Schirmeister J, Stefani F, Willmann H, Hallauer W "Renal handling of nitrofurantoin in man." Chemotherapy 5 (1965):  223-6|Goff JB, Schlegel JU, O''Dell RM "Urinary excretion of nalidixic acid, sulfamethizole and nitrofurantoin in patients with reduced renal function." J Urol 99 (1968):  371-5|Glascock HW, Jr "Nitrofurantoin in renal failure." Ann Intern Med 69 (1968):  1077|"Product Information. Macrobid (nitrofurantoin)." Procter and Gamble Pharmaceuticals  (2002):', 'DDInter', 0);
