INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13319, 22141, 'Olaparib', 'Hepatic Insufficiency', 'Olaparib primarily undergoes hepatic metabolism via CYP450 3A4 and CYP450 3A5; the majority of metabolism is through oxidation with some metabolites undergoing subsequent glucuronide or sulfate conjugation.  No dose adjustment is needed in patients with mild hepatic impairment.  It is recommended to use caution when using olaparib in patients with moderate or severe hepatic impairment as there are no data available.  Close monitoring is recommended.', '2', '"Product Information. Lynparza (olaparib)." Astra-Zeneca Pharmaceuticals  (2014):', 'DDInter', 0);
