INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13323, 22141, 'Olaparib', 'Kidney Diseases', 'In a olaparib renal impairment trial, the mean AUC increased by 24%, and the Cmax increased by 15%, in patients with mild renal impairment.  In patients with moderate renal impairment, the AUC increased by 44% and the Cmax by 26%.  No dose adjustment is needed in patients with mild renal impairment.  The dose of olaparib in patients with moderate renal impairment should be adjusted according to the prescribing information.  It is recommended to monitor for toxicity all patient with renal impairment.  The pharmacokinetics of olaparib have not been evaluated in patients with severe renal impairment or end-stage renal disease.', '2', '"Product Information. Lynparza (olaparib)." Astra-Zeneca Pharmaceuticals  (2014):', 'DDInter', 0);
