INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13325, 1166, 'Ondansetron', 'Long QT Syndrome', 'ECG changes including QT interval prolongation have been observed in patients receiving ondansetron.  Additionally, there have been some postmarketing reports of Torsade de Pointes cases.  The use of ondansetron should be avoided in patients with congenital long QT syndrome.  ECG monitoring is recommended in patients with electrolyte abnormalities such as hypokalemia or hypomagnesemia, patients with congestive heart failure, bradyarrhythmias, or patients taking other medicines that could lead to QT prolongation.', '3', '"Product Information. Zofran (ondansetron)." GlaxoSmithKline  (2001):|"Product Information. Zuplenz (ondansetron)." Strativa  Pharmaceuticals, a Division of Par Pharmaceuticals, Inc.  (2010):', 'DDInter', 0);
