INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13658, 3958, 'Orlistat', 'Diabetes Mellitus, Type 2', 'Obese, type 2 diabetic patients who achieve weight loss may demonstrate improved metabolic control of their disease as a result of their reduced weight.  Therefore, patients with type 2 diabetes mellitus should be monitored during weight-reduction therapy (or therapy that may be expected to induce significant weight loss as a secondary effect) for hypoglycemia and reduced need for oral hypoglycemic medication or insulin, and the dosages of these agents adjusted accordingly.  Patients should be apprised of the risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, and palpitation.', '2', '"Product Information. Fastin (phentermine)." SmithKline Beecham  (2001):|"Product Information. Meridia (sibutramine)." Knoll Pharmaceutical Company  (2001):|"Product Information. Xenical (orlistat)." Roche Laboratories  (2001):|"Product Information. Desoxyn (methamphetamine)." Abbott Pharmaceutical  (2001):|"Product Information. Dexedrine (dextroamphetamine)." SmithKline Beecham  (2001):|"Product Information. Didrex (benzphetamine)." Pharmacia and Upjohn  (2001):|"Product Information. Prelu-2 (phendimetrazine)." Boehringer-Ingelheim  (2001):|"Product Information. Tenuate (diethylpropion)." Aventis Pharmaceuticals  (2001):|"Product Information. Sanorex (mazindol)." Novartis Pharmaceuticals  (2001):', 'DDInter', 0);
