INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13879, 24385, 'Oxaprozin', 'Heart Failure', 'Fluid retention and edema have been observed in patients treated with nonsteroidal anti-inflammatory drugs (NSAIDs), including some topical formulations.  These drugs should be avoided in patients with severe heart failure unless the benefits are expected to outweigh the risk of worsening heart failure.  If an NSAID is used in patients with severe heart failure, they should be monitored for signs of worsening heart failure.', '2', '"Product Information. Indocin (indomethacin)." Merck & Co., Inc  (2002):|"Product Information. Naprosyn (naproxen)." Syntex Laboratories Inc  (2002):|"Product Information. Voltaren (diclofenac)." Novartis Pharmaceuticals  (2001):|"Product Information. Relafen (nabumetone)." SmithKline Beecham  (2001):|"Product Information. Feldene (piroxicam)." Pfizer U.S. Pharmaceuticals  (2001):|"Product Information. Dolobid (diflunisal)." Merck & Co., Inc  (2001):|"Product Information. Ansaid (flurbiprofen)." Pharmacia and Upjohn  (2001):|"Product Information. Lodine (etodolac)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Daypro (oxaprozin)." Searle  (2001):|"Product Information. Celebrex (celecoxib)." Searle  (2001):|"Product Information. Meclofenamate Sodium (meclofenamate)." Mylan Pharmaceuticals Inc  (2012):|"Product Information. Flector Patch (diclofenac topical)." Actavis U.S. (Alpharma USPD)  (2016):', 'DDInter', 0);
