INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (13883, 24385, 'Oxaprozin', 'Hyperkalemia', 'Increases in serum potassium concentration (including hyperkalemia) have been reported with use of nonsteroidal anti-inflammatory drugs (NSAIDs), even in some patients without renal impairment.  In patients with normal renal function, these effects have been attributed to a hyporeninemic-hypoaldosteronism state.  Caution is advised in patients with hyperkalemia.', '2', '"Product Information. Indocin (indomethacin)." Merck & Co., Inc  (2002):|"Product Information. Voltaren (diclofenac)." Novartis Pharmaceuticals  (2001):|"Product Information. Celebrex (celecoxib)." Searle  (2001):', 'DDInter', 0);
