INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (21255, 27447, 'Azilsartan medoxomil', 'Hyperkalemia', 'Drugs that inhibit the renin-angiotensin, such as angiotensin II receptor antagonist system can cause hyperkalemia.  Concomitant use of these agents with drugs that increase potassium levels may increase the risk of hyperkalemia.  Use caution when using these agents together and monitor serum potassium periodically.', '2', '"Product Information. Cozaar (losartan)." Merck & Co., Inc  (2001):|"Product Information. Diovan (valsartan)." Novartis Pharmaceuticals  (2001):|"Product Information. Avapro (irbesartan)." Bristol-Myers Squibb  (2001):|"Product Information. Teveten (eprosartan)." SmithKline Beecham  (2001):|"Product Information. Atacand (candesartan)." Astra-Zeneca Pharmaceuticals  (2001):|"Product Information. Micardis (telmisartan)." Boehringer-Ingelheim  (2001):|"Product Information. Benicar (olmesartan)." Sankyo Pharma  (2002):|"Product Information. Edarbi (azilsartan)." Takeda Pharmaceuticals America  (2011):', 'DDInter', 0);
