INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (21377, 17018, 'Ramucirumab', 'Peptic Ulcer Perforation', 'Inhibitors of vascular endothelial growth factor receptors (VEGFR) may cause gastrointestinal perforation and fistulae.  Avoid use of these agents in patients with ovarian cancer who have evidence of recto-sigmoid involvement by pelvic examination or bowel involvement on CT scan or clinical symptoms of bowel obstruction.  Permanently discontinue these agents in patients with gastrointestinal perforation.', '3', '"Product Information. Avastin (bevacizumab)." Genentech  (2004):|"Product Information. Votrient (pazopanib)." GlaxoSmithKline  (2009):|"Product Information. Cyramza (ramucirumab)." Eli Lilly and Company  (2014):', 'DDInter', 0);
