INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (21379, 17018, 'Ramucirumab', 'Hypothyroidism', 'Hypothyroidism has been reported in patients taking ramucirumab.  Therapy with ramucirumab should be administered cautiously to patients with thyroid dysfunction.  Baseline laboratory measurement of thyroid function is recommended and if appropriate, treatment per standard medical practice should be given prior to initiation of therapy.  Clinical monitoring of thyroid function is recommended during therapy.', '2', '"Product Information. Cyramza (ramucirumab)." Eli Lilly and Company  (2014):', 'DDInter', 0);
