INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22178, 3612, 'Repaglinide', 'Liver Diseases', 'Repaglinide is almost completely metabolized in the liver to pharmacologically inactive substances.  Patients with impaired liver function may be exposed to higher concentrations of repaglinide and its associated metabolites.  Higher serum drug levels, in addition to diminished gluconeogenic capacity secondary to hepatic insufficiency, may increase the potential for severe hypoglycemic episodes.  Therapy with repaglinide should be administered cautiously in patients with liver disease.  Longer intervals between dosage adjustments may be required.', '2', '"Product Information. Prandin (repaglinide)." Novo Nordisk Pharmaceuticals Inc  (2001):', 'DDInter', 0);
