INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22222, 26114, 'Rimantadine', 'Liver Diseases', 'Rimantadine is primarily metabolized by the liver.  Patients with liver disease may be at greater risk for adverse effects from rimantadine due to decreased drug clearance.  Therapy with rimantadine should be administered cautiously in patients with liver disease.  One-half the usual dosage is recommended for patients with severe hepatic dysfunction.', '2', 'Wills RJ, Belshe R, Tomlinsin D, De Grazia F, Lin A, Wells S, Milazzo J, Berry C "Pharmacokinetics of rimantadine hydrochloride in patients with chronic liver disease." Clin Pharmacol Ther 42 (1987):  449-54|"Product Information. Flumadine (riMANTAdine)." Forest Pharmaceuticals  (2022):', 'DDInter', 0);
