INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22254, 11915, 'Ribavirin', 'Lung Diseases', 'The use of aerosolized ribavirin has been associated with deterioration in pulmonary function, most significantly in patients with chronic obstructive pulmonary disease or asthma.  Respiratory status should be monitored in all patients receiving aerosolized ribavirin, but in particular, patients with underlying respiratory disorders.  Therapy should be stopped if sudden deterioration occurs, and reinstituted only with extreme caution and consideration of concomitant bronchodilator therapy.', '3', 'Hebert MF "Ribavirin." Bulletin of the Hospital Pharmacy and The Drug Information Analysis Service University of California 36 (1988):  1-4|"Product Information. Virazole (ribavirin)." ICN Pharmaceuticals Inc  (2001):|Janai HK, Marks MI, Zaleska M, Stutman HR "Ribavirin: adverse drug reactions, 1986 to 1988." Pediatr Infect Dis J 9 (1990):  209-11', 'DDInter', 0);
