INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22325, 5358, 'Rifaximin', 'Liver Diseases', 'Rifaximin is metabolized by the liver.  Following administration of rifaximin 550 mg twice daily to patients with history of hepatic encephalopathy, systemic exposure (AUC) was about 10-, 14-, and 21-fold higher in patients with mild (Child-Pugh A), moderate (Child-Pugh B), and severe (Child-Pugh C) liver dysfunction, respectively, compared to that in healthy volunteers.  No dosage adjustment is recommended because rifaximin is presumably acting locally.  Nonetheless, caution is recommended when administering rifaximin to patients with severe liver dysfunction.', '2', '"Product Information. Xifaxan (rifaximin)." Salix Pharmaceuticals  (2004):', 'DDInter', 0);
