INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22372, 0, 'Rilonacept', 'Hepatic Insufficiency', 'No formal studies have been conducted to examine the pharmacokinetics of rilonacept administered subcutaneously in patients with hepatic impairment.  Caution is advised if used on these patients.', '2', '"Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc  (2008):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22373, 0, 'Rilonacept', 'Hyperlipidemias', 'Patients treated with rilonacept experienced increases in their mean total cholesterol, HDL cholesterol, LDL cholesterol, and triglycerides.  The mean increases from baseline for total cholesterol, HDL cholesterol, LDL cholesterol, and triglycerides were 19 mg/dL, 2 mg/dL, 10 mg/dL, and 57 mg/dL respectively after 6 weeks of open-label therapy.  Physicians should monitor the lipid profiles of their patients especially those with hyperlipidemias, and consider lipid-lowering therapies as needed based upon cardiovascular risk factors and current guidelines.', '2', '"Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc  (2008):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22374, 0, 'Rilonacept', 'Infections', 'Interleukin-1 (IL-1) blockade may interfere with the immune response to infections.  Treatment with other medication as rilonacept, that work through inhibition of IL-1 has been associated with an increased risk of serious infections, and serious infections have been reported in patients taking rilonacept compared with placebo.  Rilonacept should be discontinued if a patient develops a serious infection.  Treatment with rilonacept should not be initiated in patients with an active or chronic infection.  Additionally, latent tuberculosis can be reactivated and should be treated before initiating therapy with rilonacept.', '2', '"Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc  (2008):', 'DDInter', 0);
