INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22377, 0, 'Rifabutin', 'Tuberculosis', 'Rifabutin should not be given as monotherapy in patients with active tuberculosis (TB).  The administration of rifabutin in the absence of appropriate antituberculous agents may induce organisms resistant to rifabutin and other rifamycins, including rifampin and rifapentine.  Likewise, patients must not have active TB when receiving rifabutin therapy for Mycobacterium avium complex prophylaxis or other indications where rifabutin might be used alone.  Patients who develop symptoms consistent with active TB during rifabutin therapy should be evaluated promptly so that an effective antituberculosis regimen may be instituted if necessary.', '3', '"Product Information. Mycobutin (rifabutin)." Pharmacia and Upjohn  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22378, 0, 'Rifabutin', 'Neutropenia', 'Rifabutin may occasionally cause neutropenia and rarely, thrombocytopenia.  Patients with preexisting neutropenia and/or thrombocytopenia should be monitored closely during rifabutin therapy for further decreases in blood counts, and therapy discontinued if appropriate.', '2', 'Siegal FP, Eilbott D, Burger H, et al. "Dose-limiting toxicity of rifabutin in AIDS-related complex: syndrome of arthralgia/arthritis." AIDS 4 (1990):  433-41|"Product Information. Mycobutin (rifabutin)." Pharmacia and Upjohn  (2001):', 'DDInter', 0);
