INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22405, 4364, 'Rimegepant', 'Kidney Diseases', 'Rimegepant should be avoided in patients with ESRD as it not been studied in patients with CrCl less than 15 mL/min or among patients on dialysis.  No dosage adjustment is necessary for patients with mild to severe renal impairment.  A single-dose pharmacokinetic study showed exposure to rimegepant in patients with mild or severe renal impairment was not clinically meaningful different compared to normal subjects, however, exposure in subjects with moderate renal impairment was approximately 40% higher.  In healthy male subjects, 78% of a radiolabeled dose was excreted in the feces and 24% in the urine.', '3', '"Product Information. Nurtec ODT (rimegepant)." Biohaven Pharmaceuticals  (2020):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22406, 4365, 'Rimegepant', 'Kidney Diseases', 'Rimegepant should be avoided in patients with ESRD as it not been studied in patients with CrCl less than 15 mL/min or among patients on dialysis.  No dosage adjustment is necessary for patients with mild to severe renal impairment.  A single-dose pharmacokinetic study showed exposure to rimegepant in patients with mild or severe renal impairment was not clinically meaningful different compared to normal subjects, however, exposure in subjects with moderate renal impairment was approximately 40% higher.  In healthy male subjects, 78% of a radiolabeled dose was excreted in the feces and 24% in the urine.', '3', '"Product Information. Nurtec ODT (rimegepant)." Biohaven Pharmaceuticals  (2020):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22407, 4364, 'Rimegepant', 'Liver Failure', 'Rimegepant should be avoided in patients with severe hepatic impairment.  A dedicated pharmacokinetic study has shown an approximately 2-fold increased exposure to a single-dose of rimegepant in patients with severe hepatic impairment (Child-Pugh class C).  There were no clinically meaningful differences in rimegepant exposure with mild (Child-Pugh class A) and moderate hepatic impairment (Child-Pugh class B).', '3', '"Product Information. Nurtec ODT (rimegepant)." Biohaven Pharmaceuticals  (2020):', 'DDInter', 0);
