INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22431, 22166, 'Rituximab', 'Autoimmune Diseases', 'JC virus infection resulting in Progressive Multifocal Leukoencephalopathy (PML) and death can occur in rituximab-treated patients with hematologic malignancies or with autoimmune diseases.  Consider the diagnosis of PML in any patient presenting with new-onset neurologic manifestations.  Discontinue rituximab and consider discontinuation or reduction of any concomitant chemotherapy or immunosuppressive therapy in patients who develop PML.', '3', '"Product Information. Rituxan (rituximab)." Genentech  (2001):', 'DDInter', 0);
