INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22438, 22166, 'Rituximab', 'Heart Failure', 'Cardiotoxicity has been reported in a few patients receiving rituximab infusion during clinical studies.  Patients with a history of angina or arrhythmias have experienced recurrences of these conditions during administration of rituximab.  Therapy with rituximab should be administered cautiously in patients with preexisting arrhythmias or coronary artery or ischemic heart disease.  Clinical monitoring of cardiac function throughout infusion and immediately post-infusion is recommended.', '3', '"Product Information. Rituxan (rituximab)." Genentech  (2001):', 'DDInter', 0);
