INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22452, 22166, 'Rituximab', 'Tumor Lysis Syndrome', 'Acute renal failure, hyperkalemia, hypocalcemia, hyperuricemia, or hyperphosphatemia from tumor lysis, some fatal, can occur within 12-24 hours after the first infusion of rituximab in patients with Non-Hodgkin Lymphoma (NHL).  Administer aggressive intravenous hydration and anti-hyperuricemic therapy in patients at high risk for Tumor Lysis Syndrome (TLS).  Correct electrolyte abnormalities, monitor renal function and fluid balance, and administer supportive care, including dialysis as indicated.', '3', '"Product Information. Rituxan (rituximab)." Genentech  (2001):', 'DDInter', 0);
