INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22459, 22166, 'Rituximab', 'Bone Marrow Failure Disorders', 'Myelosuppression has been reported during administration of rituximab.  Leukopenia and thrombocytopenia occur more frequently than anemia.  Severe myelosuppression has been reported rarely.  Therapy with rituximab should be administered cautiously in patients with severe bone marrow suppression.  Clinical monitoring of hematopoietic function is recommended.', '2', '"Product Information. Rituxan (rituximab)." Genentech  (2001):', 'DDInter', 0);
