INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22567, 0, 'Riociguat', 'Hypotension', 'Riociguat reduces blood pressure.  There is a potential for symptomatic hypotension or ischemia in patients with hypovolemia, severe left ventricular outflow obstruction, resting hypotension, autonomic dysfunction, or concomitant treatment with antihypertensive agents.  Caution is advised when using riociguat in these patients.', '2', '"Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc  (2013):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22568, 0, 'Riociguat', 'Liver Failure', 'The safety and efficacy of riociguat have not been demonstrated in patients with severe hepatic impairment (Child-Pugh C).  Care should be exercised when using riociguat in these patients.', '2', '"Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc  (2013):', 'DDInter', 0);
