INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22738, 839, 'Risperidone', 'Liver Diseases', 'Risperidone is metabolized by the liver to the principal active metabolite,  9-hydroxyrisperidone.  Although the pharmacokinetics of risperidone do not seem to be significantly altered in patients with liver disease, the mean free fraction of risperidone in plasma has been shown to increase by about 35% because of the diminished concentration of both albumin and alpha1-acid glycoprotein.  Therapy with risperidone should be administered cautiously in patients with severe hepatic impairment.  Lower initial dosages and slower titration are recommended.', '3', '"Product Information. Risperdal (risperidone)." Janssen Pharmaceuticals  (2001):|Mannens G, Huang ML, Meuldermans W, Hendrickx J, Woestenborghs R, Heykants J "Absorption, metabolism, and excretion of risperidone in humans." Drug Metab Dispos 21 (1993):  1134-41|Heykants J, Huang ML, Mannens G, Meuldermans W, Snoeck E, Vanbeijsterveldt L, Vanpeer A, Woestenborghs R "The pharmacokinetics of risperidone in humans - a summary." J Clin Psychiatry 55 Suppl (1994):  13-7', 'DDInter', 0);
