INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22822, 839, 'Risperidone', 'Kidney Diseases', 'Risperidone and its primary active metabolite, 9-hydroxyrisperidone, are eliminated by the kidney.  In patients with moderate to severe renal disease, clearance of the sum of parent drug and metabolite has been shown to decrease by 60% compared to that in young, healthy subjects.  Therapy with risperidone should be administered cautiously in patients with impaired renal function.  Lower initial dosages and slower titration are recommended.', '3', '"Product Information. Risperdal (risperidone)." Janssen Pharmaceuticals  (2001):|Mannens G, Huang ML, Meuldermans W, Hendrickx J, Woestenborghs R, Heykants J "Absorption, metabolism, and excretion of risperidone in humans." Drug Metab Dispos 21 (1993):  1134-41|Heykants J, Huang ML, Mannens G, Meuldermans W, Snoeck E, Vanbeijsterveldt L, Vanpeer A, Woestenborghs R "The pharmacokinetics of risperidone in humans - a summary." J Clin Psychiatry 55 Suppl (1994):  13-7', 'DDInter', 0);
