INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (23750, 17077, 'Roflumilast', 'Depressive Disorder', 'The use of roflumilast is associated with an increase in psychiatric adverse reactions.  Instances of suicidal ideation and behavior, including completed suicide, have been observed in clinical trials.  Before using roflumilast in patients with a history of depression and/or suicidal thoughts or behavior, prescribers should carefully weigh the risks and benefits of treatment in all patients.  Close monitoring for the emergence or worsening of insomnia, anxiety, depression, suicidal thoughts or other mood changes is recommended.', '2', '"Product Information. Daliresp (roflumilast)." Astra-Zeneca Pharmaceuticals  (2011):', 'DDInter', 0);
