INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (23753, 17077, 'Roflumilast', 'Weight Loss', 'Weight loss is a common adverse reaction with the use of roflumilast.  Patients treated with roflumilast should have their weight monitored regularly.  If unexplained or clinically significant weight loss occurs, weight loss should be evaluated, and discontinuation of treatment should be considered.  Care should be exercised when using this agent in patients at risk of weight loss.', '2', '"Product Information. Daliresp (roflumilast)." Astra-Zeneca Pharmaceuticals  (2011):', 'DDInter', 0);
