INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27013, 6186, 'Bempedoic acid', 'Hyperuricemia', 'Bempedoic acid can elevate serum uric acid.  Caution is advised in patients with a history of gout.  Assess uric acid levels periodically and monitor any signs or symptoms of hyperuricemia.', '2', '"Product Information. Nexletol (bempedoic acid)." Esperion Therapeutics  (2020):', 'DDInter', 0);
