INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27191, 6681, 'Terazosin', 'Liver Diseases', 'Little data exist concerning the pharmacokinetic disposition of terazosin in patients with liver disease.  Terazosin is known to be metabolized by the liver, and both parent drug (approximately 30%) and metabolites are excreted in the bile and urine.  Therapy with terazosin should be administered cautiously in patients with significantly impaired hepatic function, since drug accumulation may occur.', '2', '"Product Information. Hytrin (terazosin)." Abbott Pharmaceutical  (2001):', 'DDInter', 0);
