INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27327, 7196, 'Testosterone', 'Liver Diseases', 'The use of androgenic anabolic steroids may be associated with development of cholestatic hepatitis and jaundice.  Other, more serious hepatotoxicities such as peliosis hepatis and hepatocellular neoplasms are rare but may occur with prolonged use or high dosages.  These latter toxicities may be fatal and often not recognized until they become life-threatening.  Patients with preexisting liver diseases should be monitored more closely during therapy with anabolic steroids if they are used.  If liver function declines or toxicity occurs, therapy should be withdrawn and the cause investigated.', '3', '"Product Information. Halotestin (fluoxymesterone)." Pharmacia and Upjohn  (2001):|"Product Information. Androderm (testosterone topical)." SmithKline Beecham  (2001):', 'DDInter', 0);
