INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27805, 11385, 'Thioridazine', 'Anticholinergic Syndrome', 'Phenothiazines have anticholinergic activity, to which elderly patients are particularly sensitive.  Low-potency agents such as chlorpromazine and thioridazine tend to exhibit greater anticholinergic effects than other agents in the class.  Therapy with phenothiazines should be administered cautiously in patients with preexisting conditions that are likely to be exacerbated by anticholinergic activity, such as urinary retention or obstruction; angle-closure glaucoma, untreated intraocular hypertension, or uncontrolled primary open-angle glaucoma; and gastrointestinal obstructive disorders.', '2', '"Product Information. Thorazine (chlorpromazine)." SmithKline Beecham  (2002):|"Product Information. Sparine (promazine)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Prolixin (fluphenazine)." Bristol-Myers Squibb  (2001):|"Product Information. Compazine (prochlorperazine)." SmithKline Beecham  (2001):|"Product Information. Mellaril (thioridazine)." Sandoz Pharmaceuticals Corporation  (2001):|"Product Information. Serentil (mesoridazine)." Boehringer-Ingelheim  (2001):|"Product Information. Torecan (thiethylperazine)." Roxane Laboratories Inc  (2001):|"Product Information. Trilafon (perphenazine)." Schering Corporation  (2001):|"Product Information. Stelazine (trifluoperazine)." SmithKline Beecham  (2001):|"Product Information. Vesprin (triflupromazine)." Bristol-Myers Squibb  (2001):|Talbert RL, Yee GC, DiPiro JT, Matzke GR, Posey LM, Wells BG "Pharmacotherapy: A Pathophysiologic Approach" Stamford, CT: Appleton & Lange  (1999):', 'DDInter', 0);
