INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27929, 8509, 'Ticagrelor', 'Dyspnea', 'Dyspnea has been reported with the use of ticagrelor.  Care should be taken when prescribing this drug to patients that experienced dyspnea.  If a patient develops new, prolonged, or worsened dyspnea that is determined to be related to the drug, no specific treatment is required; continue treatment and monitoring without interruption, if possible.  In the case of intolerable dyspnea requiring discontinuation, consider prescribing another antiplatelet agent.', '2', '"Product Information. Brilinta (ticagrelor)." Astra-Zeneca Pharmaceuticals  (2011):', 'DDInter', 0);
