INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27952, 4704, 'Tinidazole', 'Liver Diseases', 'There are no data on the pharmacokinetics of tinidazole in patients with liver dysfunction.  Reduced metabolic elimination of metronidazole (a chemically-related nitroimidazole) has been reported in patients with liver dysfunction.  Usual recommended doses of tinidazole should be administered cautiously in patients with liver dysfunction.', '2', '"Product Information. Tindamax (tinidazole)." Presutti Laboratories Inc  (2004):', 'DDInter', 0);
